AOS.init();var typed=new Typed("#typed",{strings:[" <p class='about__upper-text'>Привет, Меня зовут Андрей.</p> <p class='gold-text about__main-text'>Я — FullStack разработчик.</p> <p class='about__lower-text'>На этом сайте можно ознакомиться с моими <a class='gold-text' href='#'>работами</a> и <a class='gold-text' href='#'>связаться</a> со мной.</p> <div class = 'button-wrapper'> <a class='about__button btn' href = '#contacts'>Написать мне</a> <a class='about__button btn' href = 'img/cv.pdf' download>Скачать резюме</a></div>"],typeSpeed:35});const burger=document.querySelector(".header-burger"),nav=document.querySelector(".header-links"),body=document.querySelector("body"),blackout=document.querySelector(".blackout"),burgerLine=document.querySelector(".header-burger__middle-line");burger.addEventListener("click",()=>{nav.classList.toggle("active"),body.classList.toggle("active"),burgerLine.classList.toggle("active"),blackout.classList.toggle("active")});const form=document.querySelector(".formData"),modal=document.querySelector(".modal");form.addEventListener("submit",e=>{e.preventDefault(),modal.classList.toggle("active"),body.classList.toggle("active"),form.reset()});const modalClose=document.querySelector(".modal__close");modalClose.addEventListener("click",()=>{modal.classList.toggle("active"),body.classList.remove("active")});